import React, { Fragment } from "react";
import PropTypes from "prop-types";


    const ProfileAbout = ({profile:{
        bio,
        skills,
        user: {name}
    }}) => {


        console.log(name);

  return <Fragment>
    <div className="profile-about bg-light p-2">
      {bio && (
        <Fragment>
          <h2 className="text-primary">{name.substring(0, 4)}</h2>
          <p>
           {bio}
          </p>
          <div className="line"></div>
        </Fragment>
      )}

    <h2 className="text-primary">{name}</h2>
      <div className="skills">
        <div className="p-1">
          <i className="fa fa-check"></i> HTML
        </div>
        <div className="p-1">
          <i className="fa fa-check"></i> CSS
        </div>
        <div className="p-1">
          <i className="fa fa-check"></i> JavaScript
        </div>
        <div className="p-1">
          <i className="fa fa-check"></i> Python
        </div>
        <div className="p-1">
          <i className="fa fa-check"></i> C#
        </div>
      </div>
    </div>
    </Fragment>;
};

ProfileAbout.propTypes = {
  profile: PropTypes.object.isRequired
};

export default ProfileAbout;
